var marked = require('marked');
var fs = require('fs');

function convert(fileName) {
	const readMe = fs.readFileSync('../' + fileName + '.md', 'utf-8');
	var markdownReadMe = marked(readMe);

	fs.writeFileSync('../' + fileName + '.html', markdownReadMe);
}

convert("README");
convert("Rock Paper Scissors/about");
convert("Guessing Game/about");
convert("Codecademy/about");

